import React, { useState } from 'react';
import './index.css';
import { UploadOutlined } from '@ant-design/icons';
import { Button, message, Upload } from 'antd';

const FileUploadWithIcon = () => {
  const [fileList, setFileList] = useState([]);
  const [uploading, setUploading] = useState(false);

  const handleUpload = () => {
    if (fileList.length === 0) {
      return; // Don't proceed if no file is selected
    }

    const formData = new FormData();
    formData.append('file', fileList[0]); // Only append the first file
    setUploading(true);

    // You can use any AJAX library you like
    fetch('http://localhost:8080/upload', {
      method: 'POST',
      body: formData,
    })
      .then((res) => console.log(res.json))
      .then(() => {
        setFileList([]);
        message.success('Upload successful.');
      })
      .catch(() => {
        message.error('Upload failed.');
      })
      .finally(() => {
        setUploading(false);
      });
  };

  const props = {
    onRemove: () => {
      setFileList([]); // Clear the fileList on removal
    },
    beforeUpload: (file) => {
      setFileList([file]); // Replace fileList with the selected file
      return false;
    },
    fileList,
  };

  return (
    <>
      <Upload {...props} maxCount={1}>
        <Button icon={<UploadOutlined />}>Select File</Button>
      </Upload>
      <Button
        type="primary"
        onClick={handleUpload}
        disabled={fileList.length === 0 || uploading}
        loading={uploading}
        style={{
          marginTop: 16,
        }}
      >
        {uploading ? 'Uploading' : 'Start Upload'}
      </Button>
    </>
  );
};

export default FileUploadWithIcon;
