import React, { useState } from 'react';
import './index.css';
import { UploadOutlined } from '@ant-design/icons';
import { Button, message, Upload } from 'antd';
import { Card, Modal } from 'antd';

const FileUploadWithIcon = ({ onResponse }) => {
  const [fileList, setFileList] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [data, setData] = useState([]);
  const [previewVisible, setPreviewVisible] = useState(false);
  const [previewImage, setPreviewImage] = useState('');
  const cardGroups = ["HeatMap","Feature Importance","Shap Summary Plot"];

  const handleUpload = () => {
    if (fileList.length === 0) {
      return; // Don't proceed if no file is selected
    }

    const formData = new FormData();
    formData.append('file', fileList[0]); // Only append the first file
    setUploading(true);

    // You can use any AJAX library you like
    fetch('http://localhost:8080/upload', {
      method: 'POST',
      body: formData,
    })
      .then((response) => response.json())
      .then((jsonData) => setData(jsonData))

      .then(() => {
        setFileList([]);
        message.success('Data Processed.');
      })
      .catch(() => {
        message.error('Upload failed.');
      })
      .finally(() => {
        setUploading(false);
      });
  };
  const handlePreview = (image) => {
    setPreviewVisible(true);
    setPreviewImage(image);
  };

  const handleClosePreview = () => {
    setPreviewVisible(false);
  };
  const props = {
    onRemove: () => {
      setFileList([]); // Clear the fileList on removal
    },
    beforeUpload: (file) => {
      setFileList([file]); // Replace fileList with the selected file
      return false;
    },
    fileList,
  };

  return (
    <div>
    <div className='inner-upbox'>
      <Upload {...props} maxCount={1}>
        <Button icon={<UploadOutlined />}>Select File</Button>
      </Upload>
      <Button
        type="primary"
        onClick={handleUpload}
        disabled={fileList.length === 0 || uploading}
        loading={uploading}
        style={{
          marginTop: 16,
        }}
      >
        {uploading ? 'Uploading' : 'Start Upload'}
      </Button>
    </div>
    
    <div className="down-container">
        {cardGroups.map((cardGroup, groupIndex) => (
          <div key={groupIndex} className="card-group">
            <h2 className="group-title">Title for Group {groupIndex + 1}</h2>
            <div className="card-grid">
              {cardGroup.map((card, index) => (
                <Card
                  key={index}
                  hoverable
                  style={{ width: 300 }}
                  cover={<img alt={card.title} src={`data:image/png;base64,${card.image}`} onClick={() => handlePreview(`data:image/png;base64,${card.image}`)} />}
                >
                  <Card.Meta title={card.title} />
                </Card>
              ))}
            </div>
          </div>
        ))}
      <Modal visible={previewVisible} footer={null} onCancel={handleClosePreview} width={600}>
          <img
            alt="Preview"
            style={{ width: '100%', display: 'block' }}
            src={previewImage}
          />
        </Modal>
      </div>
    </div>
  );
};

export default FileUploadWithIcon;
