import React, { useState } from 'react';
import { Card, Modal } from 'antd';
import './ColBox.css';

const ColBox = ({ data }) => {
  const [previewVisible, setPreviewVisible] = useState(false);
  const [previewImage, setPreviewImage] = useState('');

  const handlePreview = (image) => {
    setPreviewVisible(true);
    setPreviewImage(image);
  };

  const handleClosePreview = () => {
    setPreviewVisible(false);
  };

  return (
    <div className="card-grid">
      {data.map((card, index) => (
        <Card
          key={index}
          hoverable
          style={{ width: 300 }}
          cover={<img alt={`Card ${index}`} src={card.image} onClick={() => handlePreview(card.image)} />}
        >
          <Card.Meta title={card.title} description={card.description} />
        </Card>
      ))}
      <Modal visible={previewVisible} footer={null} onCancel={handleClosePreview}>
        <img alt="Preview" style={{ width: '100%' }} src={previewImage} />
      </Modal>
    </div>
  );
};

export default ColBox;
