import React, { useState } from 'react';
import './index.css';
import { UploadOutlined } from '@ant-design/icons';
import { Button, message, Upload } from 'antd';
import { Card, Modal,Row,Col } from 'antd';

const FileUploadWithIcon = ({ onResponse }) => {
  const [fileList, setFileList] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [data, setData] = useState([]);
  const [previewVisible, setPreviewVisible] = useState(false);
  const [previewImage, setPreviewImage] = useState('');

  const NamesTitles = ["HeatMap","Feature Importance","Shap Summary Plot"]
  const handleUpload = () => {
    if (fileList.length === 0) {
      return; // Don't proceed if no file is selected
    }

    const formData = new FormData();
    formData.append('file', fileList[0]); // Only append the first file
    setUploading(true);

    // You can use any AJAX library you like
    fetch('http://localhost:8080/upload', {
      method: 'POST',
      body: formData,
    })
      .then((response) => response.json())
      .then((jsonData) => setData(jsonData))

      .then(() => {
        setFileList([]);
        message.success('Data Processed.');
      })
      .catch(() => {
        message.error('Upload failed.');
      })
      .finally(() => {
        setUploading(false);
      });
  };
  const handlePreview = (image) => {
    setPreviewVisible(true);
    setPreviewImage(image);
  };

  

  const handleClosePreview = () => {
    setPreviewVisible(false);
  };
  const props = {
    onRemove: () => {
      setFileList([]); // Clear the fileList on removal
    },
    beforeUpload: (file) => {
      setFileList([file]); // Replace fileList with the selected file
      return false;
    },
    fileList,
  };

  return (
    <div>
    <div className='inner-upbox'>
      <Upload {...props} maxCount={1}>
        <Button icon={<UploadOutlined />}>Select File</Button>
      </Upload>
      <Button
        type="primary"
        onClick={handleUpload}
        disabled={fileList.length === 0 || uploading}
        loading={uploading}
        style={{
          marginTop: 16,
        }}
      >
        {uploading ? 'Uploading' : 'Start Upload'}
      </Button>
    </div>
    
    <div className='down-container'>
        {data.map((cards,index) => (
            <div key={index}>
              <h2 className='h2-center' style={{ backgroundColor: 'lightgrey', padding: '10px' }}>{NamesTitles[index]}</h2>
              <Row gutter={16} className='rowtop'>
              { cards.map((card,inx)=> (
                <Col key={inx} span={8} style={{marginTop:'10px'}}>
                  <Card
                  title={card.title}
                  bordered={false}
                  style={{ width: '100%',backgroundColor: 'lightblue' }}
                  cover={<img alt={card.title} src={`data:image/png;base64,${card.image}`} />}
                  onClick={() => handlePreview(`data:image/png;base64,${card.image}`)}
                />
                </Col>
                ))}
              
          </Row>
        
            </div>
            
        ))}
    </div>
    <Modal
      visible={previewVisible}
      title={null}
      footer={null}
      onCancel={handleClosePreview}
      centered
      width={800}
      bodyStyle={{ textAlign: 'center' }}
    >
      <img alt="Preview" src={previewImage} style={{ maxWidth: '100%', maxHeight: '80vh' }} />
    </Modal>

    </div>
  );
};

export default FileUploadWithIcon;
