import React, { useState } from 'react';
import './GreenBox.css';

const FormWithFileUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileName, setFileName] = useState('Choose a file');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setFileName(file ? file.name : 'Choose a file');

  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (selectedFile) {
      // Here, you can implement your submit logic
      console.log('Submitting file:', selectedFile.name);
    }
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} id="fileInput" className="file-input" />
        <label htmlFor="fileInput" className="upload-label">{fileName}</label>
        {selectedFile && (
          <div className="selected-file">
            Selected File: {selectedFile.name}
          </div>
        )}
        <button type="submit" className="submit-button">
          Upload
        </button>
      </form>
    </div>
  );
};

export default FormWithFileUpload;
