import React, { useState } from 'react';
import './GreenBox.css';

const FormWithFileUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileName, setFileName] = useState('Choose a file');

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    setSelectedFile(file);
    setFileName(file ? file.name : 'Choose a file');

  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (selectedFile) {
      // Here, you can implement your submit logic
      console.log('Submitting file:', selectedFile.name);
    }
  };

  return (
    <div>
                <div className="row">
                    <div className="col-md-6 offset-md-3">
                        <br /><br />

                            <h3 className="text-white">React File Upload - Nicesnippets.com</h3>
                            <br />
                            <div className="form-row">
                                <div className="form-group col-md-6">
                                    <label className="text-white">Select File :</label>
                                    <input type="file" className="form-control" name="upload_file"  />
                                </div>
                            </div>

                            <div className="form-row">
                                <div className="col-md-6">
                                    <button type="submit" className="btn btn-dark">Save</button>
                                </div>
                            </div>
                    </div>
                </div>
            </div>
  );
};

export default FormWithFileUpload;
